package com.lironglong.administrator.testgitdemo;import android.content.Intent;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.Button;import android.widget.TextView;public class MainActivity extends AppCompatActivity implements View.OnClickListener {    private Button mBt;    private TextView mTv_show;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        initView();        initEvent();    }    private void initView() {        mBt = (Button) findViewById(R.id.bt);        mTv_show = (TextView) findViewById(R.id.tv_show);    }    private void initEvent() {        mBt.setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.bt:                startActivity(new Intent(this,SecondAvtivity.class));                break;            default:                break;        }    }}