package com.lironglong.administrator.testgitdemo;import android.os.Bundle;import android.support.v7.app.AppCompatActivity;import android.view.View;import android.widget.TextView;public class MainActivity extends AppCompatActivity implements View.OnClickListener {    private TextView mTv;    private TextView mTv_show;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_main);        initView();        initEvent();    }    private void initView() {        mTv = (TextView) findViewById(R.id.tv);        mTv_show = (TextView) findViewById(R.id.tv_show);    }    private void initEvent() {        mTv.setOnClickListener(this);    }    @Override    public void onClick(View v) {        switch (v.getId()) {            case R.id.tv:                mTv.setText("你好");                break;            default:                break;        }    }}